let interval;const element=document.querySelector(".ovelny"),originalText=element.innerText,randomInt=e=>Math.floor(Math.random()*e),randomFromArray=e=>e[randomInt(e.length)],scrambleText=e=>{const t="*?><[]&@#)(.%$-_:/;?!".split("");return e.split("").map(e=>randomInt(3)>1?randomFromArray(t):e).join("")};element.addEventListener("mouseover",()=>{interval=setInterval(()=>element.innerText=scrambleText(originalText),100)}),element.addEventListener("mouseout",()=>{clearInterval(interval),element.innerText=originalText})